mapper.select:
select count(*) from board

    
        and type = #{type}
    
    
        and ${searchtype} like '%${searchcontent}%'

select num, member_id, title, content, file1 fileurl, regdate from board

    
        and num = #{num}
    
    
        and type = #{type}
    
    
        and ${searchtype} like '%${searchcontent}%'
    

order by num desc

    limit #{startrow}, #{limit}
