mapper.select:
select count(*) from item

    
        and #{category_group} like #{category_item}
    
    
        and ${searchtype} like '%${searchcontent}%'

select *, mainpic as mainpicurl from item

    
        and item_no = #{item_no}
    
    
        and name = #{name}
    
    
        and category_group_code = #{category_group} and category_item_code = #{category_item}
    
    
        and ${searchtype} like '%${searchcontent}%'
    

order by item_no

    limit #{startrow}, #{limit}
